import { createMuiTheme } from '@material-ui/core/styles';

export const theme = createMuiTheme({
  overrides: {
    MuiAppBar: {
      colorPrimary: {
        backgroundColor: '#000000',
      },
    },
    MuiButton: {
      text: {
        background: '#bc0017',
        borderRadius: 25,
        border: 0,
        color: 'white',
        height: 48,
        padding: '0 30px',
        boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
        margin: '10px',
      },
      outlined: {
        background: '#000000',
      },
      disabled: {},
    },
  },
  typography: {
    fontFamily: ['Poppins'].join(','),
  },
});
