import { createMuiTheme } from '@material-ui/core/styles';

export const theme = createMuiTheme({
  overrides: {
    MuiAppBar: {
      colorPrimary: {
        backgroundColor: '#000000',
      },
    },
    MuiPaper: {
      elevation2: {
        padding: '25px',
      },
    },
    MuiButton: {
      text: {
        background: '#bc0017',
        borderRadius: 25,
        border: 0,
        color: 'white',
        height: 48,
        padding: '0 30px',
        boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
        margin: '10px',
        '&:hover': {
          background: '#bc0017',
        },
      },
    },
    MuiLink: {
      root: {
        color: '#FFFFFF',
      },
    },
  },
  typography: {
    fontFamily: ['Poppins'].join(','),
  },
});
